import { useState, useEffect } from "react";
import React from 'react'

export default function Buyer() {

  const[buyers, setBuyers] = useState([])
 console.log(buyers)

  const customers=buyers.map(cus=>{
    return(
      <tr className="p-2 border-2">
      <td className="p-2 border-2">{cus.name.toUpper}</td>
      <td className="p-2 border-2">{cus.gender}</td>
      <td className="p-2 border-2">{cus.contact}</td>
    </tr>
    )
  })


  useEffect( () => {
    fetch("https://fashion-and-design-backend.herokuapp.com/buyers")
    .then(res => res.json())
    .then(rosemary => setBuyers(rosemary))
  },[])
  return (
    <div>
    <table>
  <tr>
    <th>Name</th>
    <th>Gender</th>
    <th>Contacts</th>
  </tr>
  {customers}


</table>
    </div>
  )
}
