    const[sellers, setSellers] = useState([])

    useEffect(()=>{
      fetch('http://localhost:9292/sellers')
      .then(res=>res.json())
      .then(data=>setSellers(data))
    },[]);
    return(
        <div>
            <section className="">

            </section>
            <section className="hero">
                <div className="rowcon">
