import React, { useState } from "react";





const sellerAPI = "http://localhost:9292/sellers";

function AddSeller ({addSeller})
 {

  const [name, setName] = useState("");
  const [speciality, setSpeciality] = useState("");



  function handleSubmit(e) {
    e.preventDefault();
    fetch(sellerAPI, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name,
        speciality,

      }),
    })
      .then((r) => r.json())
      .then((newSeller) => AddSeller(newSeller));

    setName("");
    setSpeciality("");

  }




  return (
    <div>
    <form className="seller-form" onSubmit={handleSubmit} >
      <input 
        placeholder="Name" 
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <input 
        placeholder="Speciality" 
        value={speciality}
        onChange={(e) => setSpeciality(e.target.value)}
      />


















      <input 
        type="submit" 
        value="Add-seller" 
      />
    </form>
    </div>
  );
};

export default AddSeller;
